import React from 'react';

const Hero = () => {
  return (
    <section id="home" className="bg-gradient-to-r from-blue-50 to-orange-50 py-24 px-6 text-center">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900 mb-6">
          Empowering Innovation with <span className="text-blue-600">NextGen</span> Solutions
        </h1>
        <p className="text-lg md:text-xl text-gray-700 mb-8">
          BPR NextGen Solutions is a fresh tech startup, passionate about delivering cutting-edge software tailored for your business.
        </p>
        <a href="#contact">
          <button className="bg-blue-600 text-white px-6 py-3 rounded-xl shadow hover:bg-blue-700 transition">
            Contact Us
          </button>
        </a>
      </div>
    </section>
  );
};

export default Hero;
