// ... existing imports
import React from "react";
import tech from '../assets/images/tech-team.jpeg';
import { motion } from "framer-motion";

const teamMembers = [
  {
    name: "B Prudhvi Raju",
    role: "Founder & CEO",
    bio: "A digital innovator focused on building scalable tech platforms.",
    image: "/images/team/ezaaz.jpg",
  },
  {
    name: "B Prudhvi Raju",
    role: "Founder & CEO",
    bio: "A digital innovator focused on building scalable tech platforms.",
    image: "/images/team/ezaaz.jpg",
  },
  {
    name: "B Prudhvi Raju",
    role: "Founder & CEO",
    bio: "A digital innovator focused on building scalable tech platforms.",
    image: "/images/team/ezaaz.jpg",
  },
];

const About = () => {
  return (
    <main className="bg-white text-gray-800 font-[Poppins]">
      {/* Hero Banner */}
      {/* ... existing sections */}

      {/* Why Choose Us */}
      {/* ... existing Why Choose Us section */}

      {/* Join Us / Careers */}
      <section className="py-12 px-6 md:px-20 bg-white">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-2xl font-bold text-blue-700 mb-4">Join Us</h2>
          <p className="mb-6 text-gray-700">
            Passionate about innovation? Whether you're a coder, designer, or strategist – we’re always looking for talented individuals ready to shape the digital future with us.
          </p>
          <a
            href="#careers"
            className="inline-block px-6 py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition"
          >
            Explore Careers →
          </a>
        </div>
      </section>

      {/* Open to Collaborations */}
      <section className="py-12 px-6 md:px-20 bg-blue-100">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-2xl font-bold text-blue-700 mb-4">We’re Open to Collaborations</h2>
          <p className="mb-6 text-gray-700">
            Startups, SMEs, or technology partners – if you’re working on something impactful, we’d love to explore how we can build, grow, or scale together.
          </p>
          <a
            href="#contact"
            className="inline-block px-6 py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition"
          >
            Connect with Us →
          </a>
        </div>
      </section>

      {/* Call to Action */}
      <section className="py-12 px-6 md:px-20 bg-blue-50">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-2xl font-bold text-blue-700 mb-4">Want to build smarter?</h2>
          <p className="mb-6 text-gray-700">Let’s shape the future together.</p>
          <a
            href="#contact"
            className="inline-block px-6 py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition"
          >
            Contact Us →
          </a>
        </div>
      </section>
    </main>
  );
};

export default About;
